using System.ComponentModel.Composition;
using XrmToolBox.Extensibility;
using XrmToolBox.Extensibility.Interfaces;

namespace Futurez.Xrm.Tools
{
    [Export(typeof(IXrmToolBoxPlugin)),
    ExportMetadata("BackgroundColor", "LightBlue"),
    ExportMetadata("PrimaryFontColor", "Black"),
    ExportMetadata("SecondaryFontColor", "Blue"),
    ExportMetadata("SmallImageBase64", ""),
    ExportMetadata("BigImageBase64", ""),
    ExportMetadata("Name", "Document Template Manager"),
    ExportMetadata("Description", "Utility for managing your Word and Excel Document Templates")]
    public class DocumentTemplateManager : PluginBase
    {
        public override IXrmToolBoxPluginControl GetControl()
        {
            return new DocTemplateManagerControl();
        }
    }
}
